var c=28,h=.015,t=-6,x0=0,y0=1,z0=8,x1,y1,z1,cx=250,cy=330,skale=18,drawLimit=2e4,drawCount=0;function setup(){createCanvas(500,500),colorMode(HSB,100),background("#111"),blendMode(DODGE)}function draw(){drawCount++>drawLimit&&noLoop();for(var a=0;a<20;a+=1)x1=x0+h*t*(x0-y0),y1=y0+h*(-x0*z0+c*x0-y0),z1=z0+h*(x0*y0-z0),stroke(.1*Math.abs(x1),10*Math.abs(y1),2*Math.abs(z1)),x_1=cx+x0*skale,y_1=cy+y0*skale,x_2=cx+x1*skale,y_2=cy+y1*skale,ellipse(x_2,y_2,1,1),x0=x1,y0=y1,z0=z1}